INSERT INTO parameters (cd_parameter, ds_content, ds_parameter) VALUES ('email.welcome-email', '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"> <html> <head> <meta http-equiv="Content-Type" content="text/html; charset=UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Bem vindo ao Trymee!</title> <style type="text/css"> @media(max-width:480px){   table[class=main_table],table[class=layout_table]{width:300px !important;}   table[class=layout_table] tbody tr td.header_image img{width:300px !important;height:auto !important;} } a{color:#37aadc} </style> </head> <body> <table border="0" cellpadding="0" cellspacing="0" width="100%"> <tbody> <tr> <td align="center" valign="top"> <!--  M A I N T A B L E  S T A R T  --> <table border="0" cellpadding="0" cellspacing="0" class="main_table" width="600">     <tbody>     <tr>     <td>       <!--  L A Y O U T _ T A B L E  S T A R T  -->     <table border="0" cellpadding="0" cellspacing="0" class="layout_table" style="border-collapse:collapse;border:1px solid #CCCCCC;" width="100%" >       <tbody>       <!--  H E A D E R R O W  S T A R T  -->       <tr>       <td align="left" class="header_image"><img height="64" src="https://s3.amazonaws.com/trymee-files/images/site/header-tm-64-600.png" width="600" style="border:0;display:block;"></td>       </tr>       <!--  H E A D E R R O W  E N D  -->       <tr><td style="font-size:13px;line-height:13px;margin:0;padding:0;">&nbsp;</td></tr>       <!--  B O D Y R O W  S T A R T  -->       <tr>       <td align="center" valign="top">         <table align="center" border="0" cellpadding="0" cellspacing="0" width="85%">           <tbody>           <tr>           <td align="center">             <p style="font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:22px;"> 				Caro(a) {{name}}, 				<br/> 				<br/> 				Nós do Trymee desejamos as boas vindas e agradecemos o seu cadastro. 				<br/> 				Abaixo estão os detalhes do seu cadastro:             </p> 			<p> 				<table style="font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:22px;"> 					<tr> 						<td>Login:</td> 						<td>{{email}}</td> 					</tr> 					<tr> 						<td>Plano:</td> 						<td>{{plan}}</td> 					</tr> 					<tr> 						<td>Válido até:</td> 						<td>{{endDate}}</td> 					</tr> 					<tr> 						<td>Quantidade de Testes:</td> 						<td>{{quantity}}</td> 					</tr> 					<tr> 						<td>Preço:</td> 						<td>{{price}}</td> 					</tr> 				</table> 			</p> 			<p style="font-family:Arial, Helvetica, sans-serif;font-size:14px;line-height:22px;"> 				 				Nosso objetivo é tornar os seus processos seletivos mais ágeis e produtivos, e para isso estamos a sua disposição para esclarecer qualquer dúvida e ouvir qualquer dica, crítica ou feedback. Entre em contato conosco respondendo esse e-mail ou envie para contato@trymee.com.br 				<br/> 				<br/> 				Novamente agradecemos a sua confiança e esperamos que a nossa plataforma seja útil e efetiva no seu dia a dia. 				<br/> 				<br/> 				Um grande abraço! 				Equipe Trymee App 			</p>           </td>           </tr>           </tbody>         </table>       </td>       </tr>       <!--  B O D Y R O W  E N D  -->       <tr><td style="font-size:13px;line-height:13px;margin:0;padding:0;">&nbsp;</td></tr>       <!--  F O O T E R R O W  S T A R T        <tr>       <td align="left" class="header_image"><a href="https://www.sparkpost.com/"><img height="67" src="https://www.sparkpost.com/sites/default/files/pictures/email/footer-generic-600x67.png" width="600" style="border:0;display:block;" alt="Sent via SparkPost"></a></td>       </tr> 	  -->       <!--  F O O T E R R O W  E N D  -->       </tbody>     </table>     <!--  L A Y O U T _ T A B L E  E N D  -->     </td>     </tr>     </tbody> </table> <!--  M A I N _ T A B L E  E N D  --> </td> </tr> </tbody> </table> </body> </html>', 'Template do welcome-email');
INSERT INTO parameters (cd_parameter, ds_content, ds_parameter) VALUES ('email.test-link_email', '<p>Caro(a) {{name}},</p>  <p>Este e-mail contém o link para a prova, enviado por {{sender}}. </p>  <p><a href="{{link}}">Clique aqui para iniciar a prova</a></p>  <p>Caso o link acima não esteja disponível, copie e cole o seguinte link no seu navegador: <b>{{link}}</b></p>  <p>Não responda este email. Em caso de dúvidas entre em contato com o recrutador.</p>  <p><a href="http://www.trymee.com.br">TRYMEE APP</a></p>', 'Template para test-link-email');
